import React from "react";
import { useState, useEffect } from "react";
import data from "../data";

const Crew = () => {
  const [index, setIndex] = useState(0);
  const { crew } = data[0];
  const { name, images, role, bio } = crew[index];

  useEffect(() => {
    document.body.className = "crew";
  }, []);

  return (
    <main id="main" className="grid-container-xxx grid-container--crew">
      <h1 className="numbered-title">
        <span aria-hidden="true">02</span> Meet your crew
      </h1>

      <div className="dot-indicators flex">
        <button>
          <span className="sr-only">{crew[0].role}</span>
        </button>
        <button>
          <span className="sr-only">{crew[1].role}</span>
        </button>
        <button>
          <span className="sr-only">{crew[2].role}</span>
        </button>
        <button>
          <span className="sr-only">{crew[3].role}</span>
        </button>
      </div>

      <article className="destination-info">
        <h4>{role}</h4>
        <h2 className="fs-800 ff-serif uppercase">{name}</h2>
        <p>{bio}</p>
      </article>
      <img src={images.png} alt={name} />
    </main>
  );
};

export default Crew;
