import React from "react";
import { useState, useEffect } from "react";
import data from "../data";

const Destination = () => {
  const [index, setIndex] = useState(0);
  console.log(data[index]);

  useEffect(() => {
    document.body.className = "destination";
  }, []);

  return (
    <main id="main" className="grid-container grid-container--destination flow">
      <h1 className="numbered-title">
        <span aria-hidden="true">01</span> Pick your destination
      </h1>
      {/* <img src={} alt="" /> */}

      <div class="tab-list underline-indicators flex">
        <button className="uppercase ff-sans-cond text-accent bg-dark letter-spacing-2">
          Moon
        </button>
        <button className="uppercase ff-sans-cond text-accent bg-dark letter-spacing-2">
          Mars
        </button>
        <button className="uppercase ff-sans-cond text-accent bg-dark letter-spacing-2">
          Europa
        </button>
        <button className="uppercase ff-sans-cond text-accent bg-dark letter-spacing-2">
          Titan
        </button>
      </div>

      <article className="destination-info">
        <h2 className="fs-800 ff-serif uppercase">Moon</h2>
        <p>
          See our planet as you’ve never seen it before. A perfect relaxing trip
          away to help regain perspective and come back refreshed. While you’re
          there, take in some history by visiting the Luna 2 and Apollo 11
          landing sites.
        </p>

        <div className="destination-meta flex">
          <div>
            <h3 className="fs-200 uppercase text-accent">Avg. distance</h3>
            <p className="ff-serif uppercase">384,400 km</p>
          </div>

          <div>
            <h3 className="fs-200 uppercase text-accent">Est. travel time</h3>
            <p className="ff-serif uppercase">3 days</p>
          </div>
        </div>
      </article>
    </main>
  );
};

export default Destination;
