import React from "react";
import { Link } from "react-router-dom";
import { useState } from "react";
import logo from "./assets/shared/logo.svg";

const Navbar = () => {
  const [navbarOpen, setNavbarOpen] = useState(false);
  const [menuItem, setMenuItem] = useState(0);

  const handleToggle = () => {
    setNavbarOpen(!navbarOpen);
  };

  const closeMenu = () => {
    setNavbarOpen(false);
  };

  return (
    <header className="flex primary-header">
      <a className="skip-to-content" href="#main">
        Skip to content
      </a>
      <div>
        <img src={logo} alt="space tourism logo" className="logo" />
      </div>
      <button
        onClick={handleToggle}
        className={`mobile-nav-toggle ${navbarOpen ? "show-menu" : ""}`}
        aria-controls="primary-navigation">
        <span className="sr-only" aria-expanded={navbarOpen}>
          Menu
        </span>
      </button>
      <nav>
        <ul
          id="primary-navigation"
          className={`primary-navigation underline-indicators flex 
          ${navbarOpen ? "show-menu" : ""}`}>
          <li className={`${menuItem == 0 ? "active" : ""}`}>
            <Link
              to="/"
              className="ff-sans-cond uppercase text-white letter-spacing-2"
              onClick={() => {
                closeMenu();
              }}>
              <span aria-hidden="true">00</span> Home
            </Link>
          </li>
          <li className={`${menuItem == 1 ? "active" : ""}`}>
            <Link
              to="/destination"
              className="ff-sans-cond uppercase text-white letter-spacing-2"
              onClick={() => {
                closeMenu();
              }}>
              <span aria-hidden="true">01</span> Destination
            </Link>
          </li>
          <li className={`${menuItem == 2 ? "active" : ""}`}>
            <Link
              to="/crew"
              className="ff-sans-cond uppercase text-white letter-spacing-2"
              onClick={() => {
                closeMenu();
              }}>
              <span aria-hidden="true">02</span> Crew
            </Link>
          </li>
          <li className={`${menuItem == 3 ? "active" : ""}`}>
            <Link
              to="/technology"
              className="ff-sans-cond uppercase text-white letter-spacing-2"
              onClick={() => {
                closeMenu();
              }}>
              <span aria-hidden="true">03</span> Technology
            </Link>
          </li>
        </ul>
      </nav>
    </header>
  );
};

export default Navbar;
